	//la fonction pour verifier si des valeur rempli dans le formulaire modifier ses infos sont correct ou pas 
	
	function verifier_ses_infos(){
		var faute_modifier_sesInfo=0;
		if($('#user_nom_sesInfo').val().length >= 0 && $('#user_nom_sesInfo').val().length <=20 && $('#user_nom_sesInfo').val()!=''){
			$('#user_nom_sesInfo_tip').hide();
		}else{
			$('#user_nom_sesInfo').addClass('non_Valide');
			$('#user_nom_sesInfo_tip').show();
			faute_modifier_sesInfo+=1;
		}
		if($('#user_prenom_sesInfo').val().length >= 0 && $('#user_prenom_sesInfo').val().length <=20 && $('#user_nom_sesInfo').val()!=''){
			$('#user_prenom_sesInfo_tip').hide();
		}else{
			$$('#user_prenom_sesInfo').addClass('non_Valide');
			$('#user_prenom_sesInfo_tip').show();
			faute_modifier_sesInfo+=1;
		}
		if($('#user_dateFin_sesInfo').val().match(/^[1-2]{1}[0-9]{3}[-]{1}[0-1]{1}[0-9]{1}[-][0-3]{1}[0-9]{1}/) || $('#user_dateFin_sesInfo').val()==""){
			$('#user_dateFin_sesInfo_tip').hide();
		}else{
			$('#user_dateFin_sesInfo').addClass('non_Valide');
			$('#user_dateFin_sesInfo_tip').show();
			faute_modifier_sesInfo+=1;
		}
		if($('#user_tel_sesInfo').val().match(/^0[0-9]{1} [0-9]{2} [0-9]{2} [0-9]{2} [0-9]{2}$/) || $('#user_tel_sesInfo').val()!=""){
			$('#user_tel_sesInfo_tip').hide();
		}else{
			$('#user_tel_sesInfo').addClass('non_Valide');
			$('#user_tel_sesInfo_tip').show();
			faute_modifier_sesInfo+=1;
		}
		if($('#user_fax_sesInfo').val().match(/^0[0-9]{1} [0-9]{2} [0-9]{2} [0-9]{2} [0-9]{2}$/) || $('#user_fax_sesInfo').val()!=""){
			$('#user_fax_sesInfo_tip').hide();
		}else{
			$('#user_fax_sesInfo').addClass('non_Valide');
			$('#user_fax_sesInfo_tip').show();
			faute_modifier_sesInfo+=1;
		}
		if($('#user_mail_sesInfo').val().match(/\w+([-+.]\w+)*@\w+([-.]\w+)*\.\w+([-.]\w+)*/) || $('#user_mail_sesInfo').val()!=""){
			$('#user_mail_sesInfo_tip').hide();
		
		}else{
			$('#user_mail_sesInfo').addClass('non_Valide');
			$('#user_mail_sesInfo_tip').show();
			faute_modifier_sesInfo+=1;
		}
		return faute_modifier_sesInfo;
	}
